mapper.select:
SELECT device_seq, member_id, device_token, device_version, FLOOR(DATE_FORMAT(creation_time, '%Y%m%d%H%i%s%f')/1000) as creation_time, FLOOR(DATE_FORMAT(updated_time, '%Y%m%d%H%i%s%f')/1000) as updated_time FROM device_tbl
AND member_id=#{memberId}
AND device_token=#{token}

SELECT count(*) as cnt
  FROM device_tbl
 
       
       AND member_id=#{memberId}
       
       
       AND device_token=#{token}

mapper.update:
UPDATE device_tbl
   SET updated_time=#{updatedDecimal}
         
         , device_token=#{token}
         
         
         , device_version=#{version}
         
 
    
    AND device_seq=#{seq}
    
    
    AND member_id=#{memberId}
    
    
    AND device_token=#{token}
    
 
  LIMIT 1

mapper.delete:
DELETE FROM device_tbl
 
    
    AND member_id=#{memberId}
    
    
    AND device_token=#{token}
    
 
  LIMIT 1
