mapper.select:
SELECT count(*) as cnt
  FROM device_tbl
 
       
       AND member_id=#{memberId}
       
       
       AND target_id=#{targetId}

mapper.delete:
DELETE FROM filter_tbl
 
    
    AND filter_seq=#{seq}
    
    
    AND member_id=#{memberId}
    
    
    AND target_id=#{targetId}
    
 
  LIMIT 1
